mapper.select:
WITH RECURSIVE dates AS ( SELECT DATE(#{data[0]}) AS date UNION ALL SELECT DATE_ADD(date, INTERVAL 1 DAY) FROM dates WHERE date < DATE(#{data[1]}) ) SELECT dates.date AS categories, ps.store_name AS name, COALESCE(SUM(po.order_money), 0) AS data FROM dates CROSS JOIN payment_store ps LEFT JOIN payment_order po ON po.order_store_id = ps.store_id AND DATE(po.order_createtime) = dates.date
AND ps.store_id = #{storeId}
AND ( ps.store_primary_account_id = #{userId} OR ps.store_subsidiary_account_id = #{userId} )
GROUP BY categories, name ORDER BY categories, name;

SELECT po.*, ps.* FROM payment_order po JOIN payment_store ps ON po.order_store_id = ps.store_id
po.order_createtime BETWEEN #{data[0]} AND #{data[1]} + INTERVAL 1 DAY AND ( ps.store_primary_account_id = #{userId} OR ps.store_subsidiary_account_id = #{userId} )
AND ps.store_id = #{storeId}

select po.*, ps.* FROM payment_order po JOIN payment_store ps ON po.order_store_id = ps.store_id
order_store_id = #{ordersVo.storeId}
and order_createtime BETWEEN #{ordersVo.data[0]} AND #{ordersVo.data[1]} + INTERVAL 1 DAY
