mapper.select:
SELECT * From payment_store
WHERE store_is_delete = 0

    AND user_account= (SELECT user_account FROM payment_user where user_account = userAccount)


    AND store_id_num = #{storeIdNum}


    AND storeName = #{storeName}


    AND store_is_status = #{storeIsStatus}


    AND store_createtime >= #{startTime}


    AND store_createtime <= #{endTime}
